@Keyword
def testLoginWithInvalidCredentials(TestData invalidLoginTestData) {
	WebUI.openBrowser('')  // Open browser
	WebUI.navigateToUrl(GlobalVariable.urlLogin)  // Navigate to Login page

	for (int i = 1; i < invalidLoginTestData.getRowNumbers(); i++) {
		String invalidEmail = invalidLoginTestData.getValue("username", i)
		String invalidPassword = invalidLoginTestData.getValue("password", i)

		WebUI.setText(findTestObject('Page_Login/input_email'), invalidEmail)
		WebUI.setText(findTestObject('Page_Login/input_password'), invalidPassword)
		WebUI.click(findTestObject('Page_Login/button_Login'))
	}

	if (WebUI.verifyElementPresent(findTestObject('Page_Login/button_logout'), 10, FailureHandling.OPTIONAL)) {
		KeywordUtil.markPassed('The user is not able to login with invalid credentials')  // Verify that error message appears
	} else {
		KeywordUtil.markFailed('The user is able to successfully login with invalid credentials')  // Verify unsuccessful login if error message does not appear
	}

	WebUI.closeBrowser()  // Close browser
}

def invalidLoginTestData = findTestData('Data Files/xlsx/invalid_login_data')

WebUI.comment('Test Step: Enter invalid credentials and click on the Login button')
testLoginWithInvalidCredentials(invalidLoginTestData)

@Keyword
def testLoginWithValidCredentials(TestData validLoginTestData) {
	WebUI.openBrowser('')	// Open browser
	WebUI.navigateToUrl(GlobalVariable.urlLogin) // Navigate to Login page

	for (int i = 1; i <= validLoginTestData.getRowNumbers(); i++) {
		if (i > 1) {
			WebUI.click(findTestObject('Page_Login/button_logout'))
			WebUI.navigateToUrl(GlobalVariable.urlLogin)
		}
		String validEmail = validLoginTestData.getValue("username", i)
		String validPassword = validLoginTestData.getValue("password", i)

		WebUI.setText(findTestObject('Page_Login/input_email'), validEmail)
		WebUI.setText(findTestObject('Page_Login/input_password'), validPassword)
		WebUI.click(findTestObject('Page_Login/button_login'))
		WebUI.enableSmartWait()
		// Web
		if (WebUI.verifyElementPresent(findTestObject('Page_Login/button_logout'), 10)) {
			KeywordUtil.markPassed('The user is able to successfully login with valid credentials')  // Verify successful login by waiting for Home button to appear
		} else {
			KeywordUtil.markFailedAndStop('The user is not able to login with valid credentials')  // Verify unsuccessful login if Home button does not appear
		}
	}

	WebUI.closeBrowser()  // Close browser
}
